import { useState } from "react";
import { ArrowForward, ArrowRight, MainBg, MainBtnWrapper, MainContainer, MainContent, MainH1, MainP, VideoBg,} from "./MainElements";
import Video from "../../videos/video.mp4";
import { Button } from "../ButtonElement";
import { InfoContainer, InfoRow, InfoWrapper, Column1, Column2, TextWrapper, TopLine, Heading, Subtitle, ImgWrap, Img } from "./InfoElements";
import { Link } from "react-router-dom";

const Main = ({ myurl,lightBg, lightText, darkText, alt, imgStart, img, description, headLine, topLine, id}) => {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover);
  };

  return (
    <>
    <MainContainer id="home">
      <MainBg>
        <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
      </MainBg>
      <MainContent>
        <MainH1>Decrypt üóùhe cüîíde...</MainH1>
        <MainP>
        Sign up if you're new around here
        </MainP>
        <MainBtnWrapper>
          <Button

          as={Link}      
          to="/SignUp"
          onMouseEnter={onHover}
          onMouseLeave={onHover}
          primary={true}
          dark={true}
          smooth={true}
          duration={500}
          spy={true}
          exact="true"
          offset={-80}>

            Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
          </Button>
        </MainBtnWrapper>
      </MainContent>
      </MainContainer>

      <InfoContainer lightBg={lightBg} id={id}>
        <InfoWrapper>
          <InfoRow imgStart={imgStart}>
            <Column1>

              <TextWrapper>
                <TopLine>{topLine}</TopLine>
                <Heading lightText={lightText}>{headLine}</Heading>
                <Subtitle darkText={darkText}>{description}</Subtitle>            
              </TextWrapper>
              <Button
            as={Link}      
            to={myurl}
            onMouseEnter={onHover}
            onMouseLeave={onHover}
            primary={true}
            dark={true}
            smooth={true}
            duration={500}
            spy={true}
            exact="true"
            offset={-80}
          >
            Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
          </Button>
            </Column1>
            <Column2>

              <ImgWrap>
                <Img src={img} alt={alt} />
              </ImgWrap>

            </Column2>

          </InfoRow>

        </InfoWrapper>

      </InfoContainer>

    </>
  );
};

export default Main;
