import React from 'react';
import GenListe from '../Liste/genliste'
import axios from 'axios';
import Button from '../Button/ButtonWithSpinner';

class Liste extends React.Component {

constructor(props){
  super(props);
  this.deleteUser = this.deleteUser.bind(this)
  this.state={
    utilisateurs:[]
  }
}

componentDidMount() {
  axios.get('http://localhost:4000/Select')
    .then(response => {
if (response.data.length>0){
      this.setState({
        utilisateurs:response.data})
}
    })
    .catch((error) => {
      console.log(error);
    })
}

deleteUser(id) {
  axios.delete('http://localhost:4000/Delete/'+id)
    .then(response => { console.log(response.data)});

  this.setState({
    utilisateurs: this.state.utilisateurs.filter(el => el._id !== id)
  })
}

userList(){
return this.state.utilisateurs.map(utilCourant => {
return <GenListe utilisateur={utilCourant} deleteUser={this.deleteUser}  key={utilCourant._id}/>;
})}

   render() {

    const handleLogout = () => {
      dispatch(signOutStart());
    }
    return (
      <div className="container">
      <h3>Liste des utilisateurs</h3>
      <table className="table">
      <thead className="thead-light">
       <tr>
           <th>Local</th>
           <th>Username</th>
           <th>Nom</th>
           <th>Prénom</th>
           <th>Email</th>
           <th>Password</th>

       </tr>

      </thead>

      <tbody>
    {this.userList()}

      </tbody>

      </table>
      <Button handleClick={handleLogout} label='Logout' />
      </div> 




      );
  }
}

export default Liste;
