import React from 'react';
import axios from 'axios';

class Signupclient extends React.Component {

constructor(props){
  super(props);

  this.onChangeDepartement = this.onChangeDepartement.bind(this);
  this.onChangeCode = this.onChangeCode.bind(this);
  this.onChangeNom = this.onChangeNom.bind(this);
  this.onChangePrenom = this.onChangePrenom.bind(this);
  this.onSubmit = this.onSubmit.bind(this);

  this.state={
      departement:'' , code:'', nom:'', prenom:'', deps: []
  }
}


componentDidMount() {
  axios.get('http://localhost:3015/Selectt')
    .then(response => {
      if (response.data.length > 0) {
        this.setState({
          deps: response.data.map(dep => dep.departement),
          departement: response.data[0].departement
        })
      }
    })
    .catch((error) => {
      console.log(error);
    })

}

onChangeDepartement(e){
  this.setState({
    departement:e.target.value
  })
}
onChangeCode(e){
  this.setState({
    code:e.target.value
  })
}
onChangeNom(e){
  this.setState({
    nom:e.target.value
  })
}
onChangePrenom(e){
  this.setState({
    prenom:e.target.value
  })
}

onSubmit(e){
  e.preventDefault();
  const util={
    departement:this.state.departement,
    code:this.state.code,
    nom:this.state.nom,
    prenom:this.state.prenom
  }



  console.log(util);

  axios.post('http://localhost:3015/Add', util)
  .then(res => console.log(res.data));
    window.location = '/utilisateurs';
}


  render() {
  return (
    <div className="container">
   <h3>Ajouter un utilisateur</h3>
   <form onSubmit={this.onSubmit}>
<div className="form-group">
<label>Departement :</label>
<select 
              required
              className="form-control"
              value={this.state.departement}
              onChange={this.onChangeDepartement}>
              {
                this.state.deps.map(function(dep) {
                  return <option 
                    key={dep}
                    value={dep}>{dep}
                    </option>;
                })
              }
          </select>

<label>Code :</label>
  <input type="text"
  required
  className="form-control"value={this.state.code}onChange={this.onChangeCode}/>

  <label>Nom :</label>
  <input type="text"
  required
  className="form-control" value={this.state.nom} onChange={this.onChangeNom}/>

<label>Prenom :</label>
  <input type="text"
  required
  className="form-control"value={this.state.prenom}onChange={this.onChangePrenom}/>


</div>
<div className="form-group">
  <input type="submit" value="Ajout" className="btn btn-primary"/>
</div>
   </form>
    </div>
  );
}
}

export default Signupclient;
