import { InfoContainer, InfoRow, InfoWrapper, Column1, Column2, TextWrapper, TopLine, Heading, Subtitle, ImgWrap, Img } from "./InfoElements";
import { Button } from "../ButtonElement";
import { useState } from "react";
import { ArrowForward, ArrowRight} from "../Hero/HeroElements";
import { Link } from "react-router-dom";
const InfoSection = ({ myurl,lightBg, lightText, darkText, alt, imgStart, img, description, headLine, topLine, id, linktopage}) => {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover);
  };
  return (
    <>
      <InfoContainer lightBg={lightBg} id={id}>
        <InfoWrapper>
          <InfoRow imgStart={imgStart}>
            <Column1>

              <TextWrapper>
                <TopLine>{topLine}</TopLine>
                <Heading lightText={lightText}>{headLine}</Heading>
                <Subtitle darkText={darkText}>{description}</Subtitle>            
              </TextWrapper>
              <Button
            as={Link}      
            to={myurl}
            onMouseEnter={onHover}
            onMouseLeave={onHover}
            primary={true}
            dark={true}
            height="20%"
            width="20%"
            smooth={true}
            duration={500}
            spy={true}
            exact="true"
            offset={-80}
          >
            Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
          </Button>
            </Column1>
            <Column2>

              <ImgWrap>
                <Img src={img} alt={alt} />
              </ImgWrap>

            </Column2>

          </InfoRow>

        </InfoWrapper>



      </InfoContainer>

    </>
  );
};

export default InfoSection;
