import React from 'react';
import axios from 'axios';
// import logo from './avis.jpg';
// import team from './team.gif';
import logo from '../../images/laugh.gif';

class AddStory extends React.Component {

    constructor(props) {
      super(props);
  
      this.onChangeStory = this.onChangeStory.bind(this);
      this.onChangeUser = this.onChangeUser.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
  
      this.state = {
        story: '',
        user:''
      }
    }
  
    onChangeStory(e) {
      this.setState({
        story: e.target.value
      })
    }
    onChangeUser(e) {
      this.setState({
        user: e.target.value
      })
    }

    onSubmit(e) {
      e.preventDefault();
  
      const story = {
        story: this.state.story,
        user: this.state.user
      }
  
      console.log(story);
  
      axios.post('http://localhost:3528/Addstory', story)
        .then(res => console.log(res.data));
  
      this.setState({
        story: '',
        user:''
      })
    }
  
    render() {
      return (

        <div className=".container-fluid">
      
        <div class="row">
        <div class="col-3">
        <img src={logo} alt="Logo" />
      
        </div>

        <div class="col-9"><br/>
         

        <img src={logo} alt="Logo" /><br/>
        <div class="col-9"><br/>
          <h3>Laissez-nous un retour d'information sur votre expérience.<br/> Et dites nous s'il y a des choses qu'on peut améliorer.<br/>
           </h3><br/></div>
          <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
            <div class="col-10">
              <label>Rédiger votre commentaire : </label>
          
                  
                  <textarea class="form-control" id="exampleFormControlTextarea1" rows="6" value={this.state.story}
                  onChange={this.onChangeStory}/>
                  
                  
                  </div>
<div class="col-2">
<label>Votre nom : </label>
              <input  type="text"
                  required
                  className="form-control"
                  value={this.state.user}
                  onChange={this.onChangeUser} /></div>

            </div>
            <div class="col-3">
            <div className="form-group">
              <input type="submit" value="Soumettre" className="btn btn-primary" /></div>
            </div>
          </form>
        </div>
    
        </div>
        </div>
      )
    }
  }
  
export default AddStory;
