import {useEffect, useState} from 'react';
import Button from '../Button/ButtonWithSpinner';
import ErrMsg from '../Msgs/ErrMsg';
import Ajoutlocal from '../Admin/ajoutlocal';
import {BrowserRouter as Router,Switch ,Redirect, Route} from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import {  signOutStart } from '../redux/actions/userActions';

import { selectCurrentUser,  selectErrMsg } from '../redux/selectors/userSelector';

function Gestion(props) {
	const dispatch = useDispatch();

	const currentUser = useSelector(state => selectCurrentUser(state));
	const { username } = currentUser;


	let errMsgRes = useSelector(state => selectErrMsg(state))



	const [errMsg, setErrMsg] = useState('');

	useEffect(() => {
		setErrMsg(errMsgRes)
	}, [errMsgRes])

	useEffect(() => {
		document.title = `Dashboard ${username}`;
	})





	const handleLogout = () => {
		dispatch(signOutStart());
	}

	return (
		<div className="dashboard">
			<h1>Hello {username}</h1>
			<div className='change-pass-form'>

				{
					errMsg ? <ErrMsg text={errMsg} /> : ''
				}

				<Button handleClick={handleLogout} label='Logout' />

				{/* <input type="submit" value="Ad" className="btn btn-primary"/> */}
<Button>
				<Route
                    path="/ajoutlocal"
                    component={Ajoutlocal}
                    exact 
                />
</Button>
			</div>
		</div>
	);
}

export default Gestion;
