import React from 'react';
import GenListe from '../Liste/genliste'
import axios from 'axios';
import Gestion from '../Admin/gestion';
import Comments from '../Liste/comments';
import Background from '../Liste/back.jpg';



class Liste extends React.Component {

constructor(props){
  super(props);
  this.deleteUser = this.deleteUser.bind(this)
  this.state={
    utilisateurs:[]
  }
}

componentDidMount() {
  axios.get('http://localhost:3015/Select')
    .then(response => {
if (response.data.length>0){
      this.setState({
        utilisateurs:response.data})
}
    })
    .catch((error) => {
      console.log(error);
    })
}

deleteUser(id) {
  axios.delete('http://localhost:3015/Delete/'+id)
    .then(response => { console.log(response.data)});

  this.setState({
    utilisateurs: this.state.utilisateurs.filter(el => el._id !== id)
  })
}

userList(){
return this.state.utilisateurs.map(utilCourant => {
return <GenListe utilisateur={utilCourant} deleteUser={this.deleteUser}  key={utilCourant._id}/>;
})}



   render() {




    return (
      <div     style={{
        background: `url(${Background})`,
      }}>
      <div className="container"

      style={{
        background: `url(${Background})`,
      }}>


        <br/>
      <h3>La liste des utilisateurs  </h3>
      <table className="table">
      <thead className="thead-light">
       <tr>
           <th>Ville d'abonnement</th>
           <th>Prénom</th>
           <th>Nom</th>
           <th>Surnom</th>
           <th>Email</th>
           <th>Mot de passe</th>


       </tr>

      </thead>

      <tbody>
    {this.userList()}

      </tbody>


      </table>
      <Comments />
      <Gestion />
      </div>
      </div> );
  }
}

export default Liste;
