import { useState } from "react";
import { ArrowForward, ArrowRight, MainBg, MainBtnWrapper, MainContainer, MainContent, MainH1, MainP, VideoBg,} from "./MainElements";
import Video from "../../videos/video.mp4";
import { Button } from "../ButtonElement";
import { Link } from "react-router-dom";
import Navbar from "./components/Navbar/Navbar";

function Main() {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover);
  };

  return (

    <MainContainer id="home">
      <MainBg>
        <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
      </MainBg>
      <MainContent>
        <MainH1>Decrypt 🗝he c🔒de...</MainH1>
        <MainP>
        Sign up if you're new around here
        </MainP>
        <MainBtnWrapper>
          <Button

          as={Link}      
          to="/SignUp"
          onMouseEnter={onHover}
          onMouseLeave={onHover}
          primary={true}
          dark={true}
          smooth={true}
          duration={500}
          spy={true}
          exact="true"
          offset={-80}>

            Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
          </Button>
        </MainBtnWrapper>
      </MainContent>
    </MainContainer>
  );
}

export default Main;
