import React from 'react';
import axios from 'axios';

class Updateutilisateurs extends React.Component {

  constructor(props){
    super(props);
    this.onChangeLocal = this.onChangeLocal.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeNom = this.onChangeNom.bind(this);
    this.onChangePrenom = this.onChangePrenom.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state={
     local:'' , username:'', nom:'', prenom:'',email:'',password:'' ,locals:[]
    }
  }

componentDidMount() {

  console.log(this.props.match.params.id)
  axios.get('http://localhost:4000/Select/'+this.props.match.params.id)
    .then(response => {

      this.setState({
        local: response.data.local,
        username: response.data.username,
        nom: response.data.nom,
        prenom: response.data.prenom,
        email: response.data.email,
        password: response.data.password,
        id:this.props.match.params.id
      })   })
    .catch((error)=> {
      console.log(error);
    })


    axios.get('http://localhost:4000/Selectt/')
    .then(response => {
      if (response.data.length > 0) {
        this.setState({
          deps: response.data.map(dep => dep.departement),
        })
      }
    })
    .catch((error) => {
      console.log(error);
    })





console.log(this.state.code)






}

onChangeDepartement(e){ this.setState({
  departement:e.target.value
})
}

onChangeCode(e){ this.setState({
    code:e.target.value
  })
}
onChangeNom(e){ this.setState({
    nom:e.target.value
  })
}
onChangePrenom(e){  this.setState({
    prenom:e.target.value
  })
}

onSubmit(e){ e.preventDefault();
  const util={
    departement:this.state.departement,
    code:this.state.code,
    nom:this.state.nom,
    prenom:this.state.prenom,

  }

  console.log(util);

  axios.post('http://10.30.40.121:3529/Update/'+ this.props.match.params.id, util)
  .then(res => console.log(res.data));
  window.location = '/utilisateurs';
}


  render() {
  return (
    <div className="container">
   <h3>Modifier un utilisateur</h3>
   <form onSubmit={this.onSubmit}>
<div className="form-group">

<label>Departement: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.departement}
              onChange={this.onChangeDepartement}>
              {
                this.state.deps.map(function(dep) {
                  return <option 
                    key={dep}
                    value={dep}>{dep}
                    </option>;
                })
              }
          </select>


<label>Code :</label>
  <input type="text"
  required
  className="form-control"value={this.state.code}onChange={this.onChangeCode}/>

  <label>Nom :</label>
  <input type="text"
  required
  className="form-control" value={this.state.nom} onChange={this.onChangeNom}/>

<label>Prenom :</label>
  <input type="text"
  required
  className="form-control"value={this.state.prenom}onChange={this.onChangePrenom}/>

</div>
<div className="form-group">
  <input type="submit" value="Modifier" className="btn btn-primary"/>
</div>
   </form>
    </div>
  );
}
}

export default Updateutilisateurs;
