import { useState } from "react";
import { InfoContainer1,InfoContainer2, InfoRow, InfoWrapper, Column1, Column2, TextWrapper, TopLine, Heading, Subtitle, ImgWrap, Img } from "./InfoElements";
import { ArrowForward, ArrowRight, MainBg, MainBtnWrapper, MainContainer, MainContent, MainH1, MainP, VideoBg,} from "./MainElements";
import Video from "../../videos/video.mp4";
import { Button } from "../ButtonElement";
import { Link } from "react-router-dom";
import logorooms from '../../images/stopwatchlogo.png';
import logocreep from '../../images/skulllogo.jpg';

function Main() {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover);
  };

  return (
    <div>
    <MainContainer id="home">
      <MainBg>
        <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
      </MainBg>
      <MainContent>
        <MainH1>Decrypt 🗝he c🔒de...</MainH1>
        <MainP>
        Sign up if you're new around here
        </MainP>
        <MainBtnWrapper>
          <Button

          as={Link}      
          to="/SignUp"
          onMouseEnter={onHover}
          onMouseLeave={onHover}
          primary={true}
          dark={true}
          smooth={true}
          duration={500}
          spy={true}
          exact="true"
          offset={-80}>

            Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
          </Button>
        </MainBtnWrapper>
      </MainContent>
    </MainContainer>




      <InfoContainer1 lightBg={true} id={"rooms"}>
      <InfoWrapper>
        <InfoRow imgStart={false}>
          <Column1>

            <TextWrapper>
              <TopLine>dfghjkmjnhg</TopLine>
              <Heading lightText={false}>Available Rooms</Heading>
              <Subtitle darkText={true}>fghjkjhcdfghjk</Subtitle>            
            </TextWrapper>
            <Button
          as={Link}      
          to={"/rooms"}
          onMouseEnter={onHover}
          onMouseLeave={onHover}
          primary={true}
          dark={true}
          smooth={true}
          duration={500}
          spy={true}
          exact="true"
          offset={-80}
        >
          Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
        </Button>
          </Column1>
          <Column2>

            <ImgWrap>
            <Img src={logorooms} alt="Logo" />
            </ImgWrap>

          </Column2>

        </InfoRow>




        <Column1 lightBg={true} id={"creepypastas"}>

      <TextWrapper>
        <TopLine>dfghjkmjnhg</TopLine>
        <Heading lightText={false}>Available Rooms</Heading>
        <Subtitle darkText={true}>fghjkjhcdfghjk</Subtitle>            
      </TextWrapper>
      <Button
    as={Link}      
    to={"/rooms"}
    onMouseEnter={onHover}
    onMouseLeave={onHover}
    primary={true}
    dark={true}
    smooth={true}
    duration={500}
    spy={true}
    exact="true"
    offset={-80}
  >
    Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
  </Button>
    </Column1>
    <Column2>

      <ImgWrap>
      <Img src={logocreep} alt="Logo" />
      </ImgWrap>

    </Column2>

      </InfoWrapper>



    </InfoContainer1>




    </div>
  );
}

export default Main;
