import axios from 'axios';


class ViewStory extends React.Component {

  constructor(props){
    super(props);

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeUser = this.onChangeUser.bind(this);
    this.onChangeStory = this.onChangeStory.bind(this);

    this.onSubmit = this.onSubmit.bind(this);
  
    this.state={
     title:'' , story:'', user:''
    }
  }

componentDidMount() {

  console.log(this.props.match.params.id)
  axios.get('http://localhost:3528/SelectStory/'+this.props.match.params.id)
    .then(response => {

      this.setState({
        title: response.data.title,
        story: response.data.story,
        user: response.data.user, 
        id:this.props.match.params.id
      })   })
    .catch((error)=> {
      console.log(error);
    })


console.log(this.state.id)

}



onChangeTitle(e){ this.setState({
  title:e.target.value
})
}


onChangeStory(e){ this.setState({
    story:e.target.value
  })
}
onChangeUser(e){  this.setState({
    user:e.target.value
  })
}


onSubmit(e){ e.preventDefault();
  const util={
    title:this.state.title,
    user:this.state.user,
    story:this.state.story,
    
  }

  console.log(util);

}



  render() {
  return (

<div className=".container-fluid">
      
      <div class="row">
      <div class="col-6">
      
      {/* <img src={logo} alt="Logo" /> */}

      </div>
<div class="col-3">
<br/>
    
<h2>Modifier/Mettre à jour les informations </h2><br/>
   <form onSubmit={this.onSubmit}>
<div className="form-group">

          <div class="col-12">


<label>Surnom :</label>
  <input type="text"
  required
  className="form-control"value={this.state.user}/></div>
<div class="col-12">
  <label>Nom :</label>
  <input type="text"
  required
  className="form-control" value={this.state.story} /></div>
  <div class="col-12">
  <label>Nom :</label>
  <input type="text"
  required
  className="form-control" value={this.state.title} /></div>


</div>

   </form>
    </div>
    </div>     
    </div> 


  );
}
}
  
export default ViewStory;
