import React, {useState, useEffect} from 'react';
import './Register.css';
import Input from '../Button/Input';
import Button from '../Button/ButtonWithSpinner';
import ErrMsg from '../Msgs/ErrMsg';

import { useDispatch, useSelector } from 'react-redux';
import { signUpStart } from '../redux/actions/userActions';

import { selectIsLoading, selectErrMsg } from '../redux/selectors/userSelector';
import Signupclient from './signupclient';

function Signupclient2(props) {
	const dispatch = useDispatch();

	const [input, setInput] = useState({ username: '', password: '', confirmPassword: '' });
	const { username, password, confirmPassword } = input;
	const [errMsg, setErrMsg] = useState('');

	let errMsgRes = useSelector(state => selectErrMsg(state));
	const isLoading = useSelector(state => selectIsLoading(state));

	useEffect(() => {
		document.title = 'Register';
	})

	useEffect(() => {
		setErrMsg(errMsgRes)
	}, [errMsgRes])

	const handleClick = e => {
		if (!(username && password && confirmPassword)) {
			setErrMsg('Please fill all input form.')
		} else if (password !== confirmPassword) {
			setErrMsg("Confirm password is not match.")
		} else {
			setErrMsg('');
			const newUser = { username, password };
			dispatch(signUpStart(newUser));
		}
	}

	const handleChange = e => {
		setErrMsg('');
		const { name, value } = e.target;

		setInput(prev => {
			return {
				...prev,
				[name]: value
			}
		})
	}

	return (
    <div class="col-12">
		<div className="register-page">
			<h1>Register</h1>
      <div class="col-12">
      <label>Username :</label>
      <input type="text"
  required
  className="form-control" name='username'  value={username} onChange={handleChange}/></div>
  <div class="col-12">
<label>Password :</label>
<input type="password"
  required
  className="form-control" type='password' name='password' placeholder='Password' value={password} onChange={handleChange} /></div>
			 <div class="col-12">
      <label>ConfirmPw :</label>
      <input type="text"
  required
  className="form-control" type='password' name='confirmPassword' placeholder='Confirm Password' value={confirmPassword} onChange={handleChange} /></div>
			{
				errMsg ? <ErrMsg text={errMsg} /> : ''
			}
			<Button handleClick={handleClick} isLoading={isLoading} />
		</div>		</div>
	);
}

export default Signupclient2;
