import React from 'react';
import axios from 'axios';
import Background from '../Liste/back.jpg';

class Updateutilisateurs extends React.Component {

  constructor(props){
    super(props);
    this.onChangeLocal = this.onChangeLocal.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeNom = this.onChangeNom.bind(this);
    this.onChangePrenom = this.onChangePrenom.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);


    this.onSubmit = this.onSubmit.bind(this);

    this.state={
     local:'' , username:'', nom:'', prenom:'',email:'',password:'', deps:[]
    }
  }

componentDidMount() {

  console.log(this.props.match.params.id)
  axios.get('http://localhost:3015/Select/'+this.props.match.params.id)
    .then(response => {

      this.setState({
        local: response.data.local,
        username: response.data.username,
        nom: response.data.nom,
        prenom: response.data.prenom,
        email: response.data.email,
        password: response.data.password,
        id:this.props.match.params.id
      })   })
    .catch((error)=> {
      console.log(error);
    })


    axios.get('http://localhost:3015/Selectt/')
    .then(response => {
      if (response.data.length > 0) {
        this.setState({
          deps: response.data.map(dep => dep.local),
        })
      }
    })
    .catch((error) => {
      console.log(error);
    })





console.log(this.state.id)

}

onChangeLocal(e){ this.setState({
  local:e.target.value
})
}

onChangeUsername(e){ this.setState({
  username:e.target.value
})
}


onChangeNom(e){ this.setState({
    nom:e.target.value
  })
}
onChangePrenom(e){  this.setState({
    prenom:e.target.value
  })
}

onChangeEmail(e){ this.setState({
  email:e.target.value
})
}

onChangePassword(e){ this.setState({
  password:e.target.value
})
}

onSubmit(e){ e.preventDefault();
  const util={
    local:this.state.local,
    username:this.state.username,
    nom:this.state.nom,
    prenom:this.state.prenom,
    email:this.state.email,
    password:this.state.password,

  }

  console.log(util);

  axios.post('http://localhost:3015/Update/'+ this.props.match.params.id, util)
  .then(res => console.log(res.data));
  window.location = '/utilisateurs';
}


  render() {
  return (
    <div style={{
      background: `url(${Background})`,
    }}>
    <div className="container" >
   <h3>Update un utilisateur</h3>
   <form onSubmit={this.onSubmit}>
<div className="form-group">

<label>Departement: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.local}
              onChange={this.onChangeLocal}>
              {
                this.state.deps.map(function(dep) {
                  return <option 
                    key={dep}
                    value={dep}>{dep}
                    </option>;
                })
              }
          </select>


<label>Code :</label>
  <input type="text"
  required
  className="form-control"value={this.state.username}onChange={this.onChangeUsername}/>

  <label>Nom :</label>
  <input type="text"
  required
  className="form-control" value={this.state.nom} onChange={this.onChangeNom}/>

<label>Prenom :</label>
  <input type="text"
  required
  className="form-control"value={this.state.prenom}onChange={this.onChangePrenom}/>

<label>Prenom :</label>
  <input type="text"
  required
  className="form-control"value={this.state.email}onChange={this.onChangeEmail}/>


</div>
<div className="form-group">
  <input type="submit" value="Update" className="btn btn-primary"/>
</div>
   </form>
    </div></div>
  );
}
}

export default Updateutilisateurs;
