import React from 'react';
import axios from 'axios';
import logo from './canada.jpg';

class Ajoutcomment extends React.Component {

    constructor(props) {
      super(props);

      this.onChangeComment = this.onChangeComment.bind(this);
      this.onChangeName = this.onChangeName.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state = {
        comment: '',
        name:''
      }
    }

    onChangeComment(e) {
      this.setState({
        comment: e.target.value
      })
    }
    onChangeName(e) {
      this.setState({
        name: e.target.value
      })
    }

    onSubmit(e) {
      e.preventDefault();

      const comment = {
        comment: this.state.comment,
        name: this.state.name
      }

      console.log(local);

      axios.post('http://localhost:3015/Addcomment', comment)
        .then(res => console.log(res.data));

      this.setState({
        comment: '',
        name:''
      })
    }

    render() {
      return (

        <div className=".container-fluid">

        <div class="row">
        <div class="col-6">

        <img src={logo} alt="Logo" />

        </div>

        <div class="col-4"><br/>
          <h3>Ajout comment</h3><br/>
          <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
              <label>Local: </label>
              <input  type="text"
                  required
                  className="form-control"
                  value={this.state.comment}
                  onChange={this.onChangeName} />

            </div>
            <div className="form-group">
              <input type="submit" value="Post comment" className="btn btn-primary" />
            </div>
          </form>
        </div>
        </div>
        </div>
      )
    }
  }

export default Ajoutcomment;
