import React, {useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {BrowserRouter as Router,Switch ,Redirect, Route} from "react-router-dom";
import Navbar from './components/Navbar/Navbar';
import Navbaradmin from './components/Navbar/Navbaradmin';
import Home from './components/Home/Home';
import Signupclient from './components/Client/signupclient';
import Cardrooms from './components/Client/Cardrooms';
import rooms from './components/Client/rooms';
import Signinclient from './components/Client/signinclient';
import Loginadmin from './components/Admin/loginadmin';
import Gestion from './components/Admin/gestion';
import Ajoutlocal from './components/Admin/ajoutlocal';
import Liste from './components/Liste/utilisateurs';
import Updateutilisateurs from './components/Admin/updateutilisateurs';
import { useDispatch, useSelector } from 'react-redux';
import { checkUserSession } from './components/redux/actions/userActions';
import { selectCurrentUser } from './components/redux/selectors/userSelector';
import Dashboard from './components/Dashboard/Dashboard';
import Rooms from './components/Client/rooms';
function App() {
  const dispatch = useDispatch();

  const currentUser = useSelector(state => selectCurrentUser(state));

  useEffect(() => {
    dispatch(checkUserSession())
  }, [dispatch])

  return (
      <Router>

    <Route exact path="/">
      <Redirect to="/Home"/> 
    </Route>
    <Navbar/>
    <Route path="/Home"  component={Home}/>

          {/* <Route path='/loginadmin' exact render={() => currentUser ? (<Redirect to='/gestion' />) : <Loginadmin />} />
          <Route path='/ajoutlocal'  exact render={() => currentUser ? (<Redirect to='/dashboard' />) : <Ajoutlocal />} /> 
          <Route path="/signupclient"  component={Signupclient}/>
          <Route path="/signinclient"  component={Signinclient}/>
          <Route path="/gestion"  component={Gestion}/>
          <Route path="/utilisateurs"  component={Liste}/>
          <Route path="/updateutilisateurs/:id"  component={Updateutilisateurs}/>
          <Route path='/gestion' exact  render={() => currentUser ? <Gestion />  : <Redirect to='/loginadmin' />} /> */}

  <Route   path='/loginadmin' exact render={() => currentUser ? (<Redirect to='/gestion' />) : <Loginadmin />} />
          <Route 
            path='/ajoutlocal' 
            exact 
            render={() => currentUser ? (<Redirect to='/gestion' />) : <Ajoutlocal />} 
          />
          <Route 
            path='/gestion' 
            exact 
            render={() => currentUser ? <Gestion /> : <Redirect to='/gestion' />} 
          />
<Route path="/rooms"  component={Rooms}/>
<Route path="/Cardrooms"  component={Cardrooms}/>


    </Router>

  ); }

export default App;
