import {React,useEffect, useState} from 'react';
import GenListe from '../Liste/genliste'
import axios from 'axios';
import Input from '../Button/Input';
import Button from '../Button/ButtonWithSpinner';
import ErrMsg from '../Msgs/ErrMsg';

import { useSelector, useDispatch } from 'react-redux';
import { changePasswordStart, signOutStart } from '../redux/actions/userActions';

import { selectCurrentUser, selectIsLoading, selectErrMsg } from '../redux/selectors/userSelector';




function Dashboard(props) {
	const dispatch = useDispatch();

	const currentUser = useSelector(state => selectCurrentUser(state));
	const { username } = currentUser;

	const isLoading = useSelector(state => selectIsLoading(state));
	let errMsgRes = useSelector(state => selectErrMsg(state))

	const [input, setInput] = useState({ oldPassword: '', newPassword: '' });
	const { oldPassword, newPassword } = input;
	const [errMsg, setErrMsg] = useState('');

	useEffect(() => {
		setErrMsg(errMsgRes)
	}, [errMsgRes])

	useEffect(() => {
		document.title = `Dashboard ${username}`;
	})

	const handleChange = e => {
		const { name, value } = e.target;
		setErrMsg('');

		setInput(prev => {
			return {
				...prev,
				[name]: value
			}
		})
	}

	const handleChangePassword = e => {
		if (!(oldPassword && newPassword)) {
			setErrMsg('Please fill all input form.')
		} else if (oldPassword === newPassword) {
			setErrMsg('The new password is still the same as old password.')
		} else {
			setErrMsg('');
			dispatch(changePasswordStart({ user: currentUser, oldPassword, newPassword }));
		}
	}

	const handleLogout = () => {
		dispatch(signOutStart());
	}

	return (
		<div className="dashboard">
			<h1>Hello {username}</h1>
			<div className='change-pass-form'>
				<Input type='password' name='oldPassword' placeholder='Old Password' value={oldPassword} onChange={handleChange} />
				<Input type='password' name='newPassword' placeholder='New Password' value={newPassword} onChange={handleChange} />
				{
					errMsg ? <ErrMsg text={errMsg} /> : ''
				}
				<Button handleClick={handleChangePassword} isLoading={isLoading} label='Change Password' />
				<Button handleClick={handleLogout} label='Logout' />
			</div>


      <h3>Liste des utilisateurs</h3>
     <table className="table">
       <thead className="thead-light">
        <tr>
            <th>Local</th>
            <th>Username</th>
            <th>Nom</th>
            <th>Prénom</th>
            <th>Email</th>
            <th>Password</th>

        </tr>

       </thead>

       <tbody>
     {this.userList()}

       </tbody>

       </table>


       </div>

	);
}






















class Liste extends React.Component {


constructor(props){
  super(props);
  this.deleteUser = this.deleteUser.bind(this)
  this.state={
    utilisateurs:[]
  }
}

componentDidMount() {
  axios.get('http://localhost:4000/Select')
    .then(response => {
if (response.data.length>0){
      this.setState({
        utilisateurs:response.data})
}
    })
    .catch((error) => {
      console.log(error);
    })
}

deleteUser(id) {
  axios.delete('http://localhost:4000/Delete/'+id)
    .then(response => { console.log(response.data)});

  this.setState({
    utilisateurs: this.state.utilisateurs.filter(el => el._id !== id)
  })
}

userList(){
return this.state.utilisateurs.map(utilCourant => {
return <GenListe utilisateur={utilCourant} deleteUser={this.deleteUser}  key={utilCourant._id}/>;
})}

   render() {
    return (
      <div className="container">
      <h3>Liste des utilisateurs</h3>
      <table className="table">
      <thead className="thead-light">
       <tr>
           <th>Local</th>
           <th>Username</th>
           <th>Nom</th>
           <th>Prénom</th>
           <th>Email</th>
           <th>Password</th>

       </tr>

      </thead>

      <tbody>
    {this.userList()}

      </tbody>

      </table>

      </div> );
  }
}

export default Liste;
