import React, {useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {BrowserRouter as Router,Switch, Redirect, Route} from "react-router-dom";
import Navbar from './components/Navbar/Navbar';
import Home from './components/Home/Home';
import Signupclient from './components/Signup/signupclient';
import Loginadmin from './components/Admin/loginadmin';
import Ajoutlocal from './components/Admin/ajoutlocal';
import { useDispatch, useSelector } from 'react-redux';
import { checkUserSession } from './components/redux/actions/userActions';
import { selectCurrentUser } from './components/redux/selectors/userSelector';

function App() {
  const dispatch = useDispatch();

  const currentUser = useSelector(state => selectCurrentUser(state));

  useEffect(() => {
    dispatch(checkUserSession())
  }, [dispatch])





  return (


      <Router>

<Switch>

    <Route exact path="/">
      <Redirect to="/Home"/>
    </Route>

    <Route 
            path='/loginadmin' 
            exact 
            render={() => currentUser ? (<Redirect to='/ajoutlocal'/>) : <Loginadmin/>} 
          />

    <Navbar/>
    <Route path="/Home" component={Home}/>
    <Route path="/signupclient" component={Signupclient}/>

    </Switch>

    </Router>

  );
}

export default App;
