<footer class="blockquote-footer">By: {this.state.user} ðŸ¥€<br/><br/></footer>
  <p class="mb-0">{this.state.story} </p><br/><br/>
 
</blockquote>


<br/><br/><br/><br/>






    </div> 


  );
}
}
  
export default ViewStory;
